// Used as a simple, convient 404 handler.
exports.notFound = function(req, res, message) {
  message = (message || "Not Found\n") + "";
  res.writeHead(404, {
    "Content-Type": "text/plain",
    "Content-Length": message.length
  });
  if (req.method !== "HEAD")
    res.write(message);
  res.end();
};